mapper.update:
update myty_answer
    set user_answer = #{userAnswer,jdbcType=VARCHAR}
where dm_id = #{dmId,jdbcType=INTEGER} and user_id = #{userId,jdbcType=INTEGER}

update myty_answer
    set user_judge = #{userJudge,jdbcType=INTEGER}
where dm_id = #{dmId,jdbcType=INTEGER} and user_id = #{userId,jdbcType=INTEGER}

update myty_answer
    set is_right = #{isRight,jdbcType=INTEGER}
where dm_id = #{dmId,jdbcType=INTEGER} and user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select

from myty_answer
where user_id = #{userId,jdbcType=INTEGER} and dm_id = #{dmId,jdbcType=INTEGER}
