mapper.insert:

insert into myty_dengmi_temp (dm_mimian, dm_mimu, dm_midi,
dm_mimianzhu, dm_midizhu, dm_author, dm_temp_id, agenda_round_no
)
values (#{dmMimian,jdbcType=VARCHAR}, #{dmMimu,jdbcType=VARCHAR}, #{dmMidi,jdbcType=VARCHAR},
#{dmMimianzhu,jdbcType=VARCHAR}, #{dmMidizhu,jdbcType=VARCHAR}, #{dmAuthor,jdbcType=VARCHAR},
#{dmTempId, jdbcType=INTEGER},#{agendaRoundNo, jdbcType=INTEGER}
)

mapper.select:
select

from myty_dengmi_temp
where dm_author = #{dmAuthor,jdbcType=VARCHAR} and agenda_round_no = #{agendaRoundNo,jdbcType=INTEGER}
limit #{startRow}, #{pageSize}

select

from myty_dengmi_temp
where dm_author != #{dmAuthor,jdbcType=VARCHAR}
and agenda_round_no = #{agendaRoundNo,jdbcType=INTEGER}

select a.dm_temp_id, a.dm_mimian, a.dm_mimu, a.dm_mimianzhu, b.user_answer, b.user_id
from myty_dengmi_temp a
left join myty_answer b on a.dm_temp_id = b.dm_id and b.user_id = #{dmAuthor,jdbcType=VARCHAR}
where a.dm_author != #{dmAuthor,jdbcType=VARCHAR} and a.agenda_round_no = #{agendaRoundNo,jdbcType=INTEGER}
limit #{startRow}, #{pageSize}

select count(*)
from myty_dengmi_temp a
left join myty_answer b on a.dm_temp_id = b.dm_id and b.user_id = #{dmAuthor,jdbcType=VARCHAR}
where a.dm_author != #{dmAuthor,jdbcType=VARCHAR} and a.agenda_round_no = #{agendaRoundNo,jdbcType=INTEGER}

select a.dm_temp_id, a.dm_mimian, a.dm_mimu, a.dm_midi, a.dm_mimianzhu, a.dm_midizhu, b.user_answer, b.user_id, b.user_judge
from myty_dengmi_temp a
left join myty_answer b on a.dm_temp_id = b.dm_id and b.user_id = #{dmAuthor,jdbcType=VARCHAR}
where a.dm_author != #{dmAuthor,jdbcType=VARCHAR} and a.agenda_round_no = #{agendaRoundNo,jdbcType=INTEGER}
limit #{startRow}, #{pageSize}

select count(*)
from myty_dengmi_temp a
left join myty_answer b on a.dm_temp_id = b.dm_id and b.user_id = #{dmAuthor,jdbcType=VARCHAR}
where a.dm_author != #{dmAuthor,jdbcType=VARCHAR} and a.agenda_round_no = #{agendaRoundNo,jdbcType=INTEGER}

select a.dm_temp_id, a.dm_mimian, a.dm_mimu, a.dm_midi,  b.user_answer,  b.is_right, b.user_id
from myty_dengmi_temp a
left join myty_answer b on a.dm_temp_id = b.dm_id
where a.dm_author = #{dmAuthor,jdbcType=VARCHAR}
and b.user_id != #{dmAuthor,jdbcType=VARCHAR}
and a.agenda_round_no = #{agendaRoundNo,jdbcType=INTEGER}
and b.user_answer != a.dm_midi
limit #{startRow}, #{pageSize}

select

from myty_dengmi_temp
where DM_TEMP_ID = #{dmTempId, jdbcType=INTEGER} and agenda_round_no = #{agendaRoundNo,jdbcType=INTEGER}
